def rgb_to_ycc(r, g, b):  # http://bit.ly/1blFUsF
    y = .299*r + .587*g + .114*b
    cb = 128 - .168736*r - .331364*g + .5*b
    cr = 128 + .5*r - .418688*g - .081312*b
    return y, cb, cr


def to_ycc(color):
    """ converts color tuples to floats and then to yuv """
    return rgb_to_ycc(*[x/255.0 for x in color])


def to_hsv(color):
    """ converts color tuples to floats and then to hsv """
    return rgb_to_hsv(*[x/255.0 for x in color])  # rgb_to_hsv wants floats!


def to_lab(rgb):
    color_rgb = sRGBColor(rgb[0], rgb[1], rgb[2])
    color_lab = convert_color(color_rgb, LabColor)
    lab = color_lab.lab_l, color_lab.lab_a, color_lab.lab_b
    # print(rgb, {"L": lab[0], "A": lab[1], "B": lab[2]})
    return lab


def color_dist_ycc(c1, c2):
    """ returns the squared euklidian distance between two color vectors in yuv space """
    return sum((a-b)**2 for a, b in zip(to_ycc(c1), to_ycc(c2)))


def color_dist_hsv(c1, c2):
    """ returns the squared euklidian distance between two color vectors in hsv space """
    return sum((a-b)**2 for a, b in zip(to_hsv(c1), to_hsv(c2)))


def color_dist_lab(c1, c2):
    """ returns the squared euklidian distance between two color vectors in hsv space """
    return sum((a-b)**2 for a, b in zip(to_lab(c1), to_lab(c2))), c2


def min_color_diff_hsv(color_to_match, colors):
    """ returns the `(distance, color_name)` with the minimal distance to `colors`"""
    return min(  # overal best is the best match to any color:
        # (distance to `test` color, color name)
        (color_dist_hsv(color_to_match, test), colors[test])
        for test in colors)


def min_color_diff_ycc(color_to_match, colors):
    """ returns the `(distance, color_name)` with the minimal distance to `colors`"""
    return min(  # overal best is the best match to any color:
        # (distance to `test` color, color name)
        (color_dist_ycc(color_to_match, test), colors[test])
        for test in colors)


def min_color_diff_lab(color_to_match, colors):
    """ returns the `(distance, color_name)` with the minimal distance to `colors`"""
    return min(  # overal best is the best match to any color:
        # (distance to `test` color, color name)
        (color_dist_lab(color_to_match, test), colors[test])
        for test in colors)


